private static void readDirectoryStream(Path path) {
    try (
            DirectoryStream<Path> contents = Files.newDirectoryStream(path)
    ) {
        for(Path file : contents) {
            System.out.println(file.getFileName());
            if (Files.isDirectory(file)) readDirectoryStream(file);
        }
    } catch (IOException | DirectoryIteratorException e) {
        e.printStackTrace();
    }
}

private static void readDirectoryStream(Path path) {
    DirectoryStream.Filter<Path> filter = (p) -> {
        //if p returns true it is shown (can use attributes or anything else)
        return Pattern.matches(".*\\.txt", p.toString());
    };

    try (
            DirectoryStream<Path> contents = Files.newDirectoryStream(path, filter);
    ) {
        for(Path file : contents) {
            System.out.println(file.getFileName());
            if (Files.isDirectory(file)) readDirectoryStream(file);

            //https://docs.oracle.com/javase/8/docs/api/java/nio/file/FileSystem.html#getPathMatcher-java.lang.String-
        }
    } catch (IOException | DirectoryIteratorException e) {
        e.printStackTrace();
    }
}

